import "./Cancel.scss"
import Sidebar from "../../components/sidebar/Sidebar"
import Navbar from "../../components/navbar/Navbar"
import Datatable from "../../components/datatable/Datatable"
import React,{useState} from 'react'
import axios from 'axios'

const Cancel = () => {
  const [id, setId] = useState(null)
  const [filter, setFilter] = useState(null)
  
  const all = async () => {
  const res = await axios.get("/cancel/getall")
  const data = res.data
    setId(res.data)
    
    //map data
    const mapped = data.map((item) => {
      return {
        // id: item._id,
        cancelID : item.cancelID,
      }
    })

    console.log("filter",mapped)
}

  console.log(id)

  const remove = (cancelID,id) => {
    console.log("remove id",id)
    axios.delete(`/cancel/delete/${id}`)
    .then(res => {
      console.log("deleted",res)
    })
  }

  return (
    <div className="list">
      <Sidebar/>
      <div className="listContainer">
        <Navbar/>
          <div style={{width:"100px"}}>
          <button onClick={all}>Refresh</button>
        </div>
      </div>
      <div>

        {id ? id.map(({ _id, cancelId }) => (
          // setFilter(cancelId),
          <p key={_id}> 
              <br />Reservation Id : {cancelId}
              <button  onClick={remove(cancelId)}>Delete</button>
                <hr/>
          </p>


        )) : null}
      </div>
    
    </div>
    )
}

export default Cancel;
